IF EXISTS(select 1 from INFORMATION_SCHEMA.ROUTINES where ROUTINE_NAME = 'SqlAutoEnums.Enum_Equals') DROP FUNCTION [dbo].[SqlAutoEnums.Enum_Equals];
GO
CREATE FUNCTION [dbo].[SqlAutoEnums.Enum_Equals]
(
	@enumName NVARCHAR(100)
)
RETURNS bit 
AS
BEGIN
	DECLARE @res BIT = 0;

	SET @res =	CASE
					WHEN	
							EXISTS	(
										SELECT Name AS MemberName, ID AS MemberValue FROM dbo.[SqlAutoEnums.EnumMembers_Current](@enumName)
										except
										select MemberName, MemberValue FROM dbo.[SqlAutoEnums.EnumMembers_New](@enumName)
									)
							OR
							EXISTS	(
										SELECT MemberName, MemberValue FROM dbo.[SqlAutoEnums.EnumMembers_New](@enumName)
										EXCEPT
										SELECT Name AS MemberName, ID AS MemberValue FROM dbo.[SqlAutoEnums.EnumMembers_Current](@enumName)                                      
									)                          
						THEN 0
					ELSE 1
				END;

	RETURN @res;
END

